import { useLocation, useNavigate, useParams } from "react-router-dom"

import './fullPost.css'
import AddComment from "../../components/list/components/postChip/components/postAdd/postAdd"
import List from "../../components/list/list"
import { useEffect, useState } from "react"
import fetchData from "../../utils/fetchData"
import { Post } from "../../components/list/components/postChip/post"


interface PostData {
    postId: string
    postContent: string
    postCreator: {
        creatorId: string
        creatorName: string
        creatorImg: string
    }
    postTime: string
    postVotes: number
    postImg: string
}


//TODO ADD FETCHING TO PARTICULAR POST

const FullPost = () => {
    const navigate = useNavigate()
    const location = useLocation()
    const data: PostData = location.state?.postData
    data.postImg = location.state?.postImg
    data.postCreator.creatorImg = location.state?.creatorImg

    const [commentsData, setCommentsData] = useState([])

    useEffect(() => {
        fetchData(`posts/comments/${data.postId}`, setCommentsData)
    }, [])



    return (
        <div className="full-post--page">
            <Post data={data} className={'full-post'} />
            <div className="full-post__comment--wrapper">
                <List signal="comments" className="comments" data={commentsData} />
            </div>
        </div>
    )
}

export default FullPost
