import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import fetchData from "../../utils/fetchData"
import { ImageGet } from "../../utils/ImageControl"

interface GroupBarProps {
    id: number
}

interface GroupData {
    groupName: string,
    groupAbout: string
}

function GroupBar({ id }: GroupBarProps) {
    const navigate = useNavigate()
    const [data, setData] = useState<GroupData>({ groupName: '', groupAbout: '' })
    const [avatar, setAvatar] = useState('')
    useEffect(() => {
        fetchData(`groups/profile/${id}`, setData)
        ImageGet(`groups/${id}`, setAvatar)
    }, [])

    return (
        <div className="profileInfo">
            <div className="infoblock">
                <img alt='' src={avatar} />
                <p>{data.groupName}</p>
                <div>
                    <p>About</p>
                    <div>
                        <p>{data.groupAbout}</p>
                    </div>
                </div>
                <div>
                    <button className="back-button" onClick={() => { navigate(`/events/${id}`) }}>EVENTS</button>
                </div>
            </div>
        </div>
    )
}
export default GroupBar
