import "./chatPage.css";
import List from "../../components/list/list";
import { useNavigate, useParams } from "react-router-dom";
import React, { useState, useEffect, useRef } from "react";
import fetchData from "../../utils/fetchData";
import EmojiPicker from "../../utils/emojiFuncs";
import { WebsocketContext, WebsocketContextProps } from '../../websocket/Provider'; // Adjust the path


interface messageVM {
    chatterId: number
    chatterName: string
    lastMessage: string
    messageTime: string
    type: string
}

const Chat = () => {
    const navigate = useNavigate()

    const { ready, value, send }: WebsocketContextProps = useContext(WebsocketContext);

    const [openEmoji, setEmoji] = useState(false)
    const newMessageRef = useRef<HTMLInputElement>(null)
    const [chatterAvatar, setAvatar] = useState('')
    const [getData, getChatData] = useState<messageVM[]>([])
    const [sendData, setData] = useState<{
        chatId: number;
        content: string
    }>({ chatId: 0, content: '' })
    const [error, setError] = useState('')
    const { chatId } = useParams()

    if (chatId === undefined) {
        setError('Group doesn\'t exist')
    }
    const chatString = chatId || ''
    sendData.chatId = parseInt(chatString, 10)

    useEffect(() => {
        fetchData('chats/all', getChatData)
    }, [])

    const handleMessageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { value } = e.target

        setData((prevData) => ({
            ...prevData,
            ['content']: value,
        }))
    }

    const handleSendMessage = (e: React.MouseEvent<HTMLButtonElement, MouseEvent> | React.MouseEvent<HTMLDivElement>) => {
        e.preventDefault()
        console.log(sendData)
        send('New_Message', sendData)
    }

    const handleClick = (event: React.MouseEvent<HTMLDivElement>) => {
        const parent = event.currentTarget.parentElement?.parentElement?.parentElement
        if (parent) {
            setEmoji(false)
        }
    }
    const closeBtnEmoji = (event: React.MouseEvent<HTMLButtonElement>) => {
        event.preventDefault()
        setEmoji(false)
    }
    return (
        <div className="chat__page">
            <div className="chat-window">
                <List signal={'messages'} className={'chat-feed'} data={getData} />
                <form className="chat-options">
                    <input className="chat-input" placeholder="Enter your message" ref={newMessageRef} onChange={handleMessageChange} />
                    <div className='post__comment-btn--wrapper' onClick={(e) => {
                        handleClick(e)
                        setEmoji(true)
                    }}>
                        <img className='post__new-comment-img' src='/assets/AddSmile.svg' />
                    </div>
                    <div className="emoji--wrapper">
                        {openEmoji && <EmojiPicker input={newMessageRef} closeBtn={closeBtnEmoji} className={'chat__emoji-list'} />}
                        <div className='post__comment-btn--wrapper chat' onClick={handleSendMessage}>
                            <img className='post__new-comment-img' src='/assets/Submit.svg' />
                        </div>
                    </div>
                </form>
            </div>
            <List signal={'chats'} className={'chat-list'} data={getData} />
        </div>
    );
};
export default Chat
