import React, { createContext, useContext, useRef, useEffect } from 'react';

class Event {
    type: string
    payload: any
    constructor(type: string, payload: any) {
        this.type = type
        this.payload = payload
    }
}

export const connectWS = (conn: React.MutableRefObject<WebSocket | null>) => {
    const socket = new WebSocket('ws://localhost:8080/ws')
    console.log('Attempting Websocket Connection')

    socket.onopen = () => {
        console.log('Successfully Connected')
    }

    socket.onclose = (event) => {
        console.log('socket Closed Connection: ', event)
    }

    socket.onmessage = (e) => {
        const eventData = JSON.parse(e.data)
        routeEvent(conn, eventData)

        socket.onerror = (error) => {
            console.log('socket Error: ', error)
        }
        conn.current = socket
        return () => {
            if (conn.current && conn.current.readyState === WebSocket.OPEN) {
                conn.current.close();
            }
            conn.current = null; // Reset the WebSocket reference when closed
        };
    }

    function routeEvent(conn: React.MutableRefObject<WebSocket | null>, data: any): any {
        if (data.type === undefined) {
            alert('no such event type')
        }
        switch (data.type) {
            case 'New_Message':
                break
            case 'New_Notification':
                break
            default:
                alert('Unsupported event')
        }
    }
}
