import { useEffect, useState } from "react"
import "./eventChip.css"
import { useNavigate } from "react-router-dom"

const EventChip = ({ data }: any) => {
    const [attending, setAttendance] = useState(true)
    const [pending, setPending] = useState(true)
    const navigate = useNavigate()
    useEffect(() => {
        setAttendance(data.attending)
        setPending(data.pending)
    }, [data.attending, data.pending])
    const chipClass = pending
        ? 'events-chip'
        : attending
            ? 'events-chip attending'
            : 'events-chip notattending';

    const attendingText = pending ? 'NOT GOING' : 'ATTENDING';
    const notAttendingText = 'NOT ATTENDING';

    return (
        <div className={chipClass}>
            <div className="events-chip__main">
                <div className="events-chip__title">{data.name}</div>
                <p className="events-chip__description">{data.about}</p>
                <div className="events-chip__date">{data.time}</div>
                <p className="events-chip__group" onClick={() => { navigate(`/groups/${data.groupId}`) }}>
                    GROUP: {data.groupName}
                </p>
            </div>
            <div className="events-chip__options">
                {pending && !attending && (
                    <>
                        <button className="events-chip__button" id="unattend">{notAttendingText}</button>
                        <button className="events-chip__button">GOING</button>
                    </>
                )}
                {!pending && attending && (
                    <p className="events-chip__attending">{attendingText}</p>
                )}
                {!pending && !attending && (
                    <p className="events-chip__attending not">{notAttendingText}</p>
                )}
            </div>
        </div>)
}

export default EventChip
