import { useState, useEffect } from 'react'
import '../ProfilePage.css'
import fetchData from '../../../utils/fetchData'
import { ImageGet } from '../../../utils/ImageControl'

interface ProfileBarProps {
    id: number
}

function Profilebar({ id }: ProfileBarProps) {
    const [privacy, setPrivacy] = useState('')

    const [data, setData] = useState<{
        name: string
        nickName?: string
        date: string
        avatar?: File
        about?: string
        privacy: string
    }>({
        name: '',
        date: '',
        privacy: ''
    })

    const [avatar, setAvatar] = useState('')

    useEffect(() => {
        fetchData(`users/profile/${id}`, setData)
        ImageGet(`user/${id}`, setAvatar)
    }, [])

    const handlePrivacy = () => {
        fetch('http://localhost:8080/api/users/modify', {
            method: "POST",
            credentials: "include"
        })
        fetchData(`users/profile/${id}`, setData)
    }

    return (
        <div className="profileInfo">
            <div className="infoblock">
                <img alt='' src={avatar} />
                <p style={{ margin: 0 }}>{data.name}</p>
                <div>
                    <p style={{ color: '#00ac03', marginBottom: 0 }}>Date of birth:</p>
                    <p >{data.date}</p>
                </div>
                {data.about ?
                    <div>
                        <p style={{ color: '#00ac03' }}>About: </p>
                        <div className='profile__about'>
                            <p style={{ margin: 0 }}>{data.about}</p>
                        </div>
                    </div>
                    :
                    <></>}
                <div>
                    <p style={{ color: '#00ac03', marginBottom: 0 }}>Privacy:</p>
                    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                        <p>{data.privacy}</p>
                        <button className='users-list__button' onClick={handlePrivacy}>Change</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Profilebar
