import { useState, useEffect } from 'react'
import '../ProfilePage.css'
import fetchData from '../../../utils/fetchData'
import { ImageGet } from '../../../utils/ImageControl'

interface ProfileBarProps {
    id: number
}

function Profilebar({ id }: ProfileBarProps) {
    const [privacy, setPrivacy] = useState('')

    const [data, setData] = useState<{
        name: string
        nickName?: string
        date: string
        avatar?: File
        about?: string
        privacy: string
    }>({
        name: '',
        date: '',
        privacy: ''
    })

    const [avatar, setAvatar] = useState('')

    useEffect(() => {
        fetchData(`users/profile/${id}`, setData)
        ImageGet(`user/${id}`, setAvatar)
    }, [])

    const handlePrivacy = () => {
        fetch('http://localhost:8080/api/users/modify', {
            method: "POST",
            credentials: "include"
        })
        fetchData(`users/profile/${id}`, setData)
    }

    return (
        <div className="profileInfo">
            <div className="infoblock">
                <div className='infoblock' style={{ padding: 0, gap: 0 }}>
                    <img alt='' src={avatar} />
                    <p>{data.name}</p>
                </div>
                <div>
                    <p style={{ color: '#00ac03', marginBottom: 0 }}>Date of birth:</p>
                    <p >{data.date}</p>
                </div>
                {data.about ?
                    <p>{data.about}</p>
                    :
                    <></>}
                <div>
                    <p style={{ color: '#00ac03', marginBottom: 0 }}>Date of birth:</p>
                    <p>Privacy: {data.privacy}</p>
                    <button className='change-button' onClick={handlePrivacy}>Change privacy</button>
                </div>
            </div>
        </div>
    )
}

export default Profilebar
